import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Route, Router } from '@angular/router';
import { Restaurant } from 'src/app/Interfaces/restaurant';
import { RestaurantService } from 'src/app/Services/Services/restaurant.service';

@Component({
  selector: 'app-add-restaurants',
  templateUrl: './add-restaurants.component.html',
  styleUrls: ['./add-restaurants.component.css']
})
export class AddRestaurantsComponent implements OnInit {

  addRestaurantForm!:FormGroup;// property of Formgroup use to hold and manage the form controls 

  constructor(private fb:FormBuilder, private restaurant_service:RestaurantService, private router:Router){}// fb is the instance of Formbuilder class
     // Basically fb.group is used to create form group with the specified controls, this.addRestaurantForm is used to 
     // hold the formcontrols. When the user add info the the text user adds stores in addRestuarantForm 

     ngOnInit(): void {
      this.addRestaurantForm=this.fb.group({ // creates the form group with specified controls 
        restaurant_Name: ['', Validators.required],// key and value pair 
        location: ['', Validators.required],
        food: ['', Validators.required],
        rate: [0, [Validators.required, Validators.min(0), Validators.max(5)]]
      });

    }
    onSubmit(){
      this.restaurant_service.AddRestaurants(this.addRestaurantForm.value).subscribe(data=>{// sends POST request to http which contains the form control key-pair
        this.router.navigate(['/show-restaurants']);// If the request is sucessful it navigates to the restaurant list page 
      })

      }
    }










<div class="add-restaurant-container">
    <h1>Add a New Restaurant</h1>
    <form [formGroup]="addRestaurantForm" (ngSubmit)="onSubmit()">
        <div class="form-group">
            <label for="restaurant_Name">Name:</label>
            <input type="text" id="restaurant_Name" formControlName="restaurant_Name"
                placeholder="Name of the restaurant" />
        </div>

        <div class="form-group">
            <label for="location">Location:</label>
            <input type="text" id="location" formControlName="location" placeholder="Location" />
        </div>

        <div class="form-group">
            <label for="food">Food:</label>
            <input type="text" id="food" formControlName="food" placeholder="Food" />
        </div>

        <div class="form-group">
            <label for="rate">Rate:</label>
            <input type="number" id="rate" formControlName="rate" placeholder="0 - 5" />
        </div>

        <button type="submit" class="add-btn">Add</button>
    </form>
</div>
